mapper.select:
select omisdate , omisnum , oseq , misdate, misnum, seq, cltcd, misgubun
         from tb_da026 WITH (NOLOCK)
         where misdate = #{misdate} and misnum = #{misnum}
         and cltcd = #{cltcd} and misgubun = #{misgubun}

select B.cltcd as cltcd, substring(B.misdate, 5,2)+ '.' + substring(B.misdate, 7,2) as misdate, B.misnum, B.seq, substring(B.misdate, 1,4)+ '년 ' + substring(B.misdate, 5,2)+ '월 ' + substring(B.misdate, 7,2) + '일' as misdatetxt, B.pcode, B.pname, B.psize, B.punit, B.qty, B.uamt, B.samt, B.addamt, B.amt, A.acorp, pbonsa, pmodel, pcolor , D.inname, B.misgubun, isnull(B.fixflag,'0') as fixflag, D.inname as pernm, ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS rownum, A.acorp, A.asano1 + '-' + A.asano2 + '-' + A.asano3 as asano, A.aname, A.aupte, A.ajong, A.ajuso1 + ' ' + A.ajuso2 as ajuso, A.atelno, F.acorp as eeacorp, F.asano as eeasano, F.aname as eeaname, F.aupte as eeupte, F.ajong as eeajong, F.ajuso as eeajuso, F.atelno as eeatelno from TB_DA024 B WITH (NOLOCK) , cif A WITH (NOLOCK) , TB_DA023 C WITH (NOLOCK) Left outer JOIN INSA D ON C.perid = D.insano , ( select acorp, atelno, asano1 + '-' + asano2 + '-' + asano3 as asano, aname, aupte, ajong, ajuso1 + ' ' + ajuso2 as ajuso from cif where acorp1 + acorp2 + acorp3 = '0000' ) F where B.misdate + B.misnum = C.misdate + C.misnum AND B.misgubun = C.misgubun AND B.cltcd = A.acorp1 + CAST(A.acorp2 as varchar) AND C.cltcd = A.acorp1 + CAST(A.acorp2 as varchar) AND B.misdate + B.misnum + B.seq + B.cltcd in
#{itemcd}
AND B.fixflag = '1' ORDER BY B.misdate, B.misnum, B.seq

mapper.delete:
delete from tb_da023
where misdate = #{misdate} and misnum = #{misnum} and cltcd = #{cltcd} and misgubun = #{misgubun}
  and  (select count(*) as cnt  from tb_da024 with (NOLOCK) where misdate = #{misdate} and misnum=#{misnum} and misgubun = #{misgubun} and cltcd = #{cltcd} ) = 0

delete from tb_da023
where  cltcd = #{cltcd} and misgubun = #{misgubun} and misdate = #{omisdate} and misnum=#{omisnum}
  and  (select count(*) as cnt  from tb_da024 with (NOLOCK) where misdate = #{omisdate} and misnum=#{omisnum} and misgubun = #{misgubun} and cltcd = #{cltcd} ) = 0

delete from tb_da024
where  cltcd = #{cltcd} and misgubun = #{misgubun}
  and  misdate + misnum + seq  = ( #{omisdate} + #{omisnum} + #{oseq} ) and isnull(fixflag,'0') = '0'

delete from tb_da025
where misdate = #{misdate} and misnum = #{misnum} and cltcd = #{cltcd} and misgubun = #{misgubun}
  and misdate + misnum not in (select misdate + misnum from tb_da026 where misdate = #{misdate} and misnum=#{misnum} and misgubun = #{misgubun} )

mapper.update:
UPDATE tb_da024 SET
fixflag=#{fixflag}
where misdate=#{misdate} and misnum=#{misnum} and  seq=#{seq} and cltcd = #{cltcd} and misgubun = #{misgubun}

UPDATE tb_da026 SET
fixflag=#{fixflag}, omisdate=#{omisdate}, omisnum=#{omisnum}, oseq=#{oseq}
where misdate=#{misdate} and misnum=#{misnum} and  seq=#{seq} and cltcd = #{cltcd} and misgubun = #{misgubun}
